import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
    const [jobs, setJobs] = useState([]);
    const [selectedJob, setSelectedJob] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        // 模拟 API 数据
        const mockJobs = [
            {
                title: "データ分析・シミュレーション",
                company: "東京ガス株式会社",
                salary: "年収 900万円〜1,200万円",
                location: "東京都 港区 海岸",
                type: "正社員",
                features: ["リモートワーク可", "フレックスタイム制", "育児休暇あり"],
            },
            {
                title: "サステナビリティ推進",
                company: "株式会社ニップツ",
                salary: "年収 600万円〜800万円",
                location: "東京都 千代田区 麹町",
                type: "正社員",
                features: ["寮・社宅あり", "完全週休二日制", "第二新卒歓迎"],
            },
        ];

        // 模拟 API 延迟
        setTimeout(() => {
            setJobs(mockJobs);
            setLoading(false);
        }, 1000);
    }, []);

    return (
        <div className="container">
            <div className="job-list">
                <h2>おすすめの求人</h2>
                {loading ? (
                    <p>読み込み中...</p>
                ) : (
                    jobs.map((job, index) => (
                        <div
                            key={index}
                            className={`job-card ${selectedJob === job ? "selected" : ""}`}
                            onClick={() => setSelectedJob(job)}
                        >
                            <h3>{job.title}</h3>
                            <p>{job.company}</p>
                            <p>{job.salary}</p>
                            <p>{job.location}</p>
                            <span className="badge">{job.type}</span>
                        </div>
                    ))
                )}
            </div>

            <div className="job-details">
                {selectedJob ? (
                    <>
                        <h2>{selectedJob.title}</h2>
                        <p><strong>会社:</strong> {selectedJob.company}</p>
                        <p><strong>年収:</strong> {selectedJob.salary}</p>
                        <p><strong>勤務地:</strong> {selectedJob.location}</p>
                        <p><strong>雇用形態:</strong> {selectedJob.type}</p>
                        <h3>特長</h3>
                        <ul>
                            {selectedJob.features.map((feature, idx) => (
                                <li key={idx}>{feature}</li>
                            ))}
                        </ul>
                    </>
                ) : (
                    <p>求人を選択してください</p>
                )}
            </div>
        </div>
    );
}

export default App;
